import React, {useState} from 'react';
import {FaArrowCircleUp} from 'react-icons/fa';

const ScrollUpButton = () => {

    const scrollToTop = () =>{
      window.scrollTo({top: 0, behavior: 'smooth'});
    };

    return (
        <FaArrowCircleUp 
        className="ScrollButton" 
        onClick={scrollToTop} 
        style={{height: 40}}/>
  );
}

export default ScrollUpButton
