import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import PhotoGrid from './Components/UI/PhotoGrid';
import Header from './Components/UI/Header';
import ScrollUpButton from './Components/UI/ScrollUpButton';


const App = () => {
  const [imageList, setImageList] = useState([]);
  const [isLoading, setIsLoading] = useState(true)
  const API_KEY = '4fdxFtNkZwq8H36afmKh1ImHEnzcQG95nAN9W28e';
  const COUNT_VAL = 100;

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true)
      const response = await axios(
        `https://api.nasa.gov/planetary/apod?count=${COUNT_VAL}&thumbs=true&api_key=${API_KEY}`
      )

      console.log(response.data)
      setImageList(response.data)
      setIsLoading(false)
    }
    fetchData()
  }, [])

  function refreshPage() {
    window.location.reload(false);
  }

  const [visible, setVisible] = useState(false)

  const toggleVisible = () => {
    const scrolled = document.documentElement.scrollTop;
    if (scrolled > 300){
      setVisible(true)
    } 
    else if (scrolled <= 300){
      setVisible(false)
    }
  };

  const scrollUp = () =>{
    window.scrollTo({
      top: 0, 
      behavior: 'smooth'
    });
  };

  window.addEventListener('scroll', toggleVisible);

  return (
    <div  className="container">
      <Header/>
      <PhotoGrid isLoading={isLoading} images={imageList} />
      <div className='footer'>
        <button className="LoadBtn" onClick={refreshPage}>Refresh...</button>
        <ScrollUpButton />
      </div>

    </div>
  );
}

export default App;
