import React from 'react'
import NASAdata from './NASAdata'
import Masonry from "react-responsive-masonry"
import {useState} from 'react'
import slice from 'lodash';


const PhotoGrid = ({ images }) => {
  const [searchTerm, setSearchTerm]=useState('')
  return (
    <div>
      <div className = "center">
        <input 
          className="searchBar" 
          placeholder="Search Date or Title..."
          onChange = {(event) => {
            setSearchTerm(event.target.value);
          }}
        /> 
      </div>

      <Masonry columnsCount={2} gutter={4}>
          {images.filter((image)=>{
            if(searchTerm == ""){
              return image
            } else if (image.date.includes(searchTerm)){
              return image
            } else if (image.title.toLowerCase().includes(searchTerm.toLowerCase())){
              return image
            }
          }).slice(0, 10).map((image, key) => (
            <NASAdata key={image.public_id} image={image}></NASAdata>
          ))}

      </Masonry>

      <div className='footer'>
        <button className="LoadButton" >Load More...</button>
      </div>

    </div>
  )
}

export default PhotoGrid
