/*
Description: Constructor PhotoGrid is the component responsible for creating the masonry layout grid consisting of NASAdata objects. The NASAdata objects consist of all the info (and images) from the NASA APOD API formated in a list along with the like button. The PhotoGrid constructor takes those objects and maps them so that they can form an array to be displayed on the web application. The search bar is also included in this constructor (for simplicity sake). The searchbar function filters the image list and ensures that only NASAdata objects that match the search entered by the user are displayed.

Author: Nourjehan Faris
Version: 2022-01-18
*/
import React from 'react'
import NASAdata from './NASAdata'
import Loading from './Loading'
import Masonry from "react-responsive-masonry"
import {useState} from 'react'

const PhotoGrid = ({ images, isLoading}) => {
  const [searchTerm, setSearchTerm]=useState('')
  return isLoading ? (
    <Loading/>
  ) : (
    <div>
        <div className = "center">
          <input 
            className="searchBar" 
            placeholder="Search Date or Title..."
            onChange = {(event) => {
              setSearchTerm(event.target.value);
            }}
          /> 
        </div>

      <Masonry columnsCount={2} gutter={4}>
          {images.filter((image)=>{
            if(searchTerm == ""){
              return image
            } else if (image.date.includes(searchTerm)){
              return image
            } else if (image.title.toLowerCase().includes(searchTerm.toLowerCase())){
              return image
            }
          }).map((image, key) => (
            <NASAdata key={image.public_id} image={image}></NASAdata>
          ))}

      </Masonry>
    </div>
  )
}

export default PhotoGrid
