import React, {useState} from 'react';
import {FaArrowCircleUp} from 'react-icons/fa';

const ScrollButton = () =>{

  const scrollToTop = () =>{
    window.scrollTo({
      top: 0, 
      behavior: 'smooth'
    });
  };

  return (
    <button className="ScrollButton">
     <FaArrowCircleUp onClick={scrollToTop}/>
    </button>
  );
}

export default ScrollButton;
