import React, {useState, useEffect} from 'react';
import {
  slice, concat, 
} from 'lodash';
import axios from 'axios';
import './App.css';
import PhotoGrid from './Components/UI/PhotoGrid';
import Header from './Components/UI/Header';


const App = () => {
  const [imageList, setImageList] = useState(slice([],0,LIMIT));
  const [showMore,setShowMore] = useState(true);
  const [index,setIndex] = useState(LIMIT);

  const API_KEY = '4fdxFtNkZwq8H36afmKh1ImHEnzcQG95nAN9W28e';
  const LENGTH = 100;
  const LIMIT = 10;

  const loadMore = () =>{
    const newIndex = index + LIMIT;
    const newShowMore = newIndex < (LENGTH - 1);
    const newImageList = concat(imageList, slice([], index, newIndex));
    setIndex(newIndex);
    setImageList(newImageList);
    setShowMore(newShowMore);
  }

  useEffect(() => {
    const fetchData = async () => {

      const response = await axios(
        `https://api.nasa.gov/planetary/apod?count=10&thumbs=true&concept_tags=True&api_key=${API_KEY}`
      )

      console.log(response.data)

      setImageList(response.data)
    }

    fetchData()
  }, [])

  return (
    <div  className="container">
      <Header/>
      <PhotoGrid images={imageList} />
      {showMore && <button onClick={loadMore}> Load More </button>}
    </div>
  );
}

export default App;
