import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './App.css';
import PhotoGrid from './Components/UI/PhotoGrid';
import Header from './Components/UI/Header';


const App = () => {
  state = {
    count: 10
  }
  const [imageList, setImageList] = useState([]);
  const API_KEY = '4fdxFtNkZwq8H36afmKh1ImHEnzcQG95nAN9W28e';
  loadMore = () => {
    this.setState((prevState) => ({
      page: prevState.page + 1
    }));
  };
  useEffect(() => {
    const {count} = this.state;

    const fetchData = async () => {

      const response = await axios(
        `https://api.nasa.gov/planetary/apod?count=${count}&concept_tags=True&api_key=${API_KEY}`
      )

      console.log(response.data)

      setImageList(response.data)
    }

    fetchData()
  }, [])

  return (
    <div  className="container">
      <Header/>
      <PhotoGrid images={imageList} />
      <div className="load-more">
        <button onClick={this.loadMore} className="btn-grad">
          'Load More'
        </button>
      </div>
    </div>
  );
}

export default App;
